import type { ComponentStory, ComponentMeta } from '@storybook/react';
import React from 'react';

import { Avatar, Box, Margins } from '../..';

export default {
  title: 'Data Display/Avatar',
  component: Avatar,
} as ComponentMeta<typeof Avatar>;

const sizes: (
  | 'x16'
  | 'x18'
  | 'x20'
  | 'x24'
  | 'x28'
  | 'x32'
  | 'x36'
  | 'x40'
  | 'x48'
  | 'x124'
  | 'x200'
  | 'x332'
)[] = ['x16', 'x18', 'x28', 'x32', 'x36', 'x48', 'x124', 'x200', 'x332'];

const Template: ComponentStory<typeof Avatar> = (args) => (
  <Margins all='x16'>
    {sizes.map((size, i) => (
      <Box display='inline-flex' verticalAlign='middle' key={i}>
        <Avatar url={args.url} size={size} rounded={args.rounded} />
      </Box>
    ))}
  </Margins>
);

const imgUrl =
  'data:image/jpeg;base64,';

export const Default = Template.bind({});
Default.args = {
  url: imgUrl,
};

export const Rounded = Template.bind({});
Rounded.args = {
  url: imgUrl,
  rounded: true,
};

const StackTemplate: ComponentStory<typeof Avatar> = (args) => (
  <Margins all='x16'>
    {sizes.map((size, i) => (
      <Box key={i}>
        <Avatar.Stack className={args.className}>
          <Avatar url={args.url} size={size} rounded={args.rounded} />
          <Avatar url={args.url} size={size} rounded={args.rounded} />
          <Avatar url={args.url} size={size} rounded={args.rounded} />
        </Avatar.Stack>
      </Box>
    ))}
  </Margins>
);

export const Stack = StackTemplate.bind({});
Stack.args = {
  url: imgUrl,
};

export const StackRounded = StackTemplate.bind({});
StackRounded.args = {
  url: imgUrl,
  rounded: true,
};
